import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ApiService } from '../../core/services/api.service';

@Component({
  selector: 'app-register',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './register.component.html'
})
export class RegisterComponent {
  private api = inject(ApiService);
  email = ''; name = ''; password = '';
  async submit(){
    await this.api.register({ email:this.email, name:this.name, password:this.password }).toPromise();
    alert('Registered. Please login.');
  }
}

<div class="card">
  <h1>Register</h1>
  <form (ngSubmit)="submit()">
    <p><input [(ngModel)]="name" name="name" placeholder="name"></p>
    <p><input [(ngModel)]="email" name="email" placeholder="email"></p>
    <p><input [(ngModel)]="password" name="password" placeholder="password" type="password"></p>
    <p><button type="submit">Create account</button></p>
  </form>
</div>
