import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { AuthService } from '../../core/services/auth.service';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './login.component.html'
})
export class LoginComponent {
  private auth = inject(AuthService);
  email = '';
  password = '';
  login(){ this.auth.loginWithCredentials(this.email, this.password); }
  google(){ this.auth.googleLoginRedirect(); }
}

<div class="card">
  <h1>Login</h1>
  <form (ngSubmit)="login()">
    <p><input [(ngModel)]="email" name="email" placeholder="email"></p>
    <p><input [(ngModel)]="password" name="password" placeholder="password" type="password"></p>
    <p>
      <button type="submit">Login</button>
      <button type="button" (click)="google()">Login with Google</button>
    </p>
  </form>
  <a routerLink="/reset">Forgot password?</a>
</div>
