import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ApiService } from '../../core/services/api.service';

@Component({
  selector: 'app-reset',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './reset.component.html'
})
export class ResetComponent {
  private api = inject(ApiService);
  email = '';
  token = '';
  newPassword = '';

  request(){
    this.api.requestReset(this.email).subscribe(()=> alert('If the email exists, a reset link was sent.'));
  }
  change(){
    if(!this.token || !this.newPassword){ alert('token and newPassword required'); return; }
    this.api.resetPassword({ token:this.token, password:this.newPassword }).subscribe(()=> alert('Password changed.'));
  }
}

<div class="card">
  <h1>Password reset</h1>
  <h3>Request reset</h3>
  <p><input [(ngModel)]="email" name="email" placeholder="email"></p>
  <p><button (click)="request()">Send reset email</button></p>
  <h3>Change with token</h3>
  <p><input [(ngModel)]="token" name="token" placeholder="token from email"></p>
  <p><input [(ngModel)]="newPassword" name="newPassword" type="password" placeholder="new password"></p>
  <p><button (click)="change()">Change password</button></p>
</div>
